* {
  box-sizing: border-box;
  margin: 0;
  padding: 0;
}

html {
  --bg: #3a4070;
  --secondary: #182649;
  --main-font-color: #ffffff;
  --teal: #4fcdd0;
  --lime: #65f55b;
  --purple: #924fd0;
  --yellow: #ffe607;
  --red: #dc1111;
  --light-mode-bg-off-white: #ececec;
  --light-mode-secondary-white: #fefeff;
  --accent-grey: #999;
  --dark-box-shadow: 0 -2px 10px rgba(29, 33, 66, 1);
  --submit-btn-bg-color: #eee;
  --search-icon-color: #182649;
}

html[data-theme="light-mode"] {
  --bg: #ececec;
  --secondary: #fefeff;
  --main-font-color: #111;
  --dark-box-shadow: 0 -2px 10px rgba(227, 225, 225, 1);
  --submit-btn-bg-color: #acacac;
}

.hidden-elements {
  visibility: hidden;
  display: none;
}

body {
  background-color: var(--bg);
  color: var(--main-font-color);
  font-family: Arial, Helvetica, sans-serif;
  padding-top: 10vh;
  width: 100%;
  overflow-x: hidden;
}

/* theme switch */

.switch {
  position: relative;
  display: inline-block;
  width: 4.5vw;
  height: 5vh;
}

.switch input {
  opacity: 0;
  width: 0;
  height: 0;
}

.slider {
  position: absolute;
  cursor: pointer;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  background-color: var(--secondary);
  -webkit-transition: 0.4s;
  transition: 0.4s;
}

.slider:before {
  position: absolute;
  content: "";
  height: 4.5vh;
  width: 2vw;
  left: 4px;
  bottom: 2px;
  background-color: #acacac;
  -webkit-transition: 0.4s;
  transition: 0.4s;
}

input:checked + .slider {
  background-color: var(--light-mode-secondary-white);
}

input:focus + .slider {
  box-shadow: 0 0 1px var(--secondary);
}

input:checked + .slider:before {
  -webkit-transform: translateX(2vw);
  -ms-transform: translateX(2vw);
  transform: translateX(2vw);
}

input:checked + .slider:after {
  background-color: var(--bg);
}

.slider.round {
  border-radius: 34px;
}

.slider.round:before {
  border-radius: 50%;
}

#search-form {
  width: 25vw;
  height: 5vh;
  position: relative;
}

.search-bar {
  border-radius: 30px;
  border: none;
  background-color: var(--secondary);
  color: var(--accent-grey);
  width: inherit;
  height: 5vh;
  padding-bottom: 3vh;
  padding-top: 3vh;
  padding-left: 2vw;
  display: block;
}

#submit-btn {
  position: absolute;
  right: 0.3vw;
  top: 10%;
  width: 2.5vw;
  height: 100%;
  border-radius: 50%;
  border: none;
  background-color: var(--submit-btn-bg-color);
  /* background: url("/assets/loading.gif"); */
}

.visible-icon {
  visibility: visible;
}

.hidden-icon {
  visibility: hidden;
  display: none;
}

#submit-btn:hover {
  cursor: pointer;
}

.material-icons {
  color: var(--search-icon-color);
}

.search-bar:focus {
  border-color: #999;
  border-radius: 30px;
}

.calorie-amount {
  padding-top: 1vh;
  font-size: 2.5rem;
}
.calories-text {
  font-size: 1.5rem;
}

.measurement-amount {
  padding-top: 1vh;
  font-size: 2.5rem;
}

.measurement-text {
  font-size: 1.5rem;
}

.glycemic-index-amount {
  padding-top: 1vh;
  font-size: 1.5rem;
}

.grid-container {
  width: 100%;
  height: 150vh;
  display: grid;
  grid-template-areas:
    "theme-switch . heading-and-search heading-and-search . daily-value "
    ". . heading-and-search heading-and-search . daily-value"
    ". . food-item-heading food-item-heading . daily-value"
    "calories measurement macro-breakdown macro-breakdown macro-breakdown daily-value"
    "calories-per-macro calories-per-macro micro-by-gram micro-by-gram micro-by-gram daily-value"
    "credit . . . . .";
  grid-template-columns: 1fr 1fr 1fr 1fr 1fr 1fr;
  grid-template-rows: 1fr 0.5fr 0.5fr 1.5fr 1.5fr 1fr;
  padding-left: 5vw;
  padding-right: 5vw;
  grid-gap: 0.5vw;
}

.theme-switch {
  grid-area: theme-switch;
  height: 5vh;
  width: 5vw;
}
.heading-and-search-bar {
  grid-area: heading-and-search;
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: space-evenly;
  padding-bottom: 15vh;
}

.food-item-heading {
  grid-area: food-item-heading;
  text-align: center;
}

.calories-box {
  grid-area: calories;
  background-color: var(--secondary);
  border-radius: 30px;
  height: 24vh;
  width: 12vw;
  text-align: center;
  padding-top: 5vh;
  box-shadow: var(--dark-box-shadow);
}
.measurement-box {
  grid-area: measurement;
  background-color: var(--secondary);
  border-radius: 30px;
  height: 24vh;
  width: 12vw;
  text-align: center;
  padding-top: 5vh;
  box-shadow: var(--dark-box-shadow);
}
.glycemic-index-box {
  grid-area: glycemic-index;
  background-color: var(--secondary);
  border-radius: 30px;
  height: 18vh;
  width: 9vw;
  text-align: center;
  padding-top: 5vh;
}
.macro-nutrient-percentage {
  grid-area: macro-breakdown;
  background-color: var(--secondary);
  border-radius: 30px;
  box-shadow: var(--dark-box-shadow);
  position: relative;
  width: 40vw;
  height: auto;
}

.calories-per-macro-nutrient {
  grid-area: calories-per-macro;
  background-color: var(--secondary);
  border-radius: 30px;
  display: flex;
  flex-direction: column;
  justify-content: space-around;
  text-align: center;
  box-shadow: var(--dark-box-shadow);
}
.micro-nutrients-by-gram {
  grid-area: micro-by-gram;
  background-color: var(--secondary);
  border-radius: 30px;
  box-shadow: var(--dark-box-shadow);
  position: relative;
  width: 40vw;
  height: 38vh;
}

.percentage-of-daily-value-box {
  grid-area: daily-value;
  height: max-content;
  width: 20vw;
  background-color: var(--secondary);
  border-radius: 30px;
  line-height: 3.5rem;
  box-shadow: var(--dark-box-shadow);
}

.percent-value-and-description {
  text-align: center;
}

.macro-groups {
  display: flex;
  flex-direction: row;
  margin-bottom: 5vh;
}

.calories-per-macro-heading {
  margin-top: 5vh;
}

.calories-from-carbs-group {
  color: var(--teal);
}

.calories-from-protein-group {
  color: var(--lime);
}

.calories-from-fat-group {
  color: var(--purple);
}

.percentage-of-daily-value-heading {
  line-height: 2rem;
  padding-top: 5vh;
}

.value-description {
  color: #999;
  font-style: italic;
  padding-left: 1vw;
  padding-right: 1vw;
  line-height: 1.5rem;
  margin-top: 5vh;
}

.nutrient-and-percent-values {
  display: flex;
  flex-direction: revert;
  justify-content: space-between;
}

.nutrient-name {
  padding-left: 1vw;
}

.nutrient-value {
  padding-right: 1vw;
}

#edamam-badge {
  grid-area: credit;
  height: 5vh;
  width: 5vw;
}

a img {
  width: 20vw;
  height: 10vh;
}
